import '../css/Carousel.css'
import React from 'react'
import { Carousel } from 'flowbite-react'

export default class MyCarousel extends React.Component {
  constructor (props) {
    super(props)
    this.state = { }
  }

  disableScroll = (e) => {
    e.preventDefault()
  }

  componentDidUpdate (prevProps, prevState) {
    console.log(prevProps)
  }

  render () {
    return <><div id={this.props.id} className="observed image-container flex flex-col m-auto gap-6 w-full md:flex-row md:w-11/12">
      <Carousel slideInterval={5000}>
        {this.props.items.map(
          (item, index) => <><img key={`${index}-url`} src={item.url} alt="..." style={{ pointerEvents: 'none' }} onScroll={this.disableScroll} className="h-fit overscroll-y-none w-full" />
          <figcaption key={`${index}-legend`} className="absolute px-4 text-sm font-bold text-white bottom-16 legend md:text-xl">
            <p key={`${index}-p`}>{item.message}</p>
          </figcaption></>
        )}
      </Carousel>
      <aside className=" first-line:uppercase first-line:tracking-widest first-letter:text-xl first-letter:font-bold first-letter:text-white w-full text-sm text-white font-bold rounded-lg bg-black opacity-90 p-6 md:w-1/2 md:first-letter:text-7xl animate__animated animate__backInLeft aside-photo">{this.props.text}</aside>
      </div>
      </>
  }
}
